using System.Collections;using System.Collections.Generic;using UnityEngine;public class MoveScript : MonoBehaviour {    protected Animator animator;    float speed = 1.5f;    float balloonSpeed = 1.0f;    private GameObject balloon;    // Use this for initialization    void Start () {        animator = GetComponent<Animator>();        foreach (Transform child in gameObject.transform)
        {
            if (child.name == "Balloon")
            {
                balloon = child.gameObject;
            }
        }    }		// Update is called once per frame	void Update () {        if (Input.GetKey(KeyCode.LeftArrow) || Input.GetAxis("LeftJoystickX") > 0) //left and right are for player character        {            transform.position += Vector3.left * speed * Time.deltaTime;            /*animator.SetBool("left", true);            animator.SetBool("up", false);            animator.SetBool("right", false);            animator.SetBool("blush", false);            */        }        if (Input.GetKey(KeyCode.RightArrow) || Input.GetAxis("LeftJoystickX") < 0) // figure out joystick        {            transform.position += Vector3.right * speed * Time.deltaTime;           /* animator.SetBool("right", true);            animator.SetBool("up", false);            animator.SetBool("left", false);            animator.SetBool("blush", false);            */        }        if (Input.GetKey(KeyCode.UpArrow)) //up and down for NPC character        {            balloon.transform.position += Vector3.up * balloonSpeed * Time.deltaTime;        }        if (Input.GetKey(KeyCode.DownArrow))        {
            balloon.transform.position += Vector3.down * balloonSpeed * Time.deltaTime;        }    }}